mapper.update:
update kpg_task
set
	task_status=#{taskStatus.id}
 where
 	id=#{id}

update kpg_task
set
	kpg_key_amount=#{kpgKeyAmount}
 where
 	id=#{id}

mapper.select:
SELECT t1.id AS id, t1. NAME AS NAME, t1.kpg_algorithm AS keyPairAlgorithm, t1.kpg_key_amount AS kpgKeyAmount, t1.task_status AS taskStatus, t1.task_start_time AS taskStartTime, t1.task_notes AS taskNotes, t1.exe_task_start_time AS exeTaskStartTime, t1.exe_task_end_time AS exeTaskEndTime, t1.dbcommit_bufsize AS dbCommitBufsize, t1.generated_key_amount generatedKeyAmount, t1.task_exe_result AS taskExeResult, t2.id AS kpgAlgId, t2. NAME AS algName, t2.alg_oid AS algorithmOid, t2.alg_name AS algorithmName, t2.keysize AS keysize, t2.notes AS notes, t2.is_valid AS isValid, t3.id AS SysCodeId, t3.para_code AS paraCode, t3.para_value AS paraValue, t3.para_type AS paraType, t3.is_valid AS SysIsValid, t3.notes AS SysNotes, t5.id AS SysCodeIdDb, t5.para_code AS paraCodeDb, t5.para_value AS paraValueDb, t5.para_type AS paraTypeDb, t5.is_valid AS SysIsValidDb, t5.notes AS SysNotesDb, t4.id AS TypeId, t4.para_type AS typeParaType, t4.notes AS typeNotes, t6.id AS TypeIdDb, t6.para_type AS typeParaTypeDb, t6.notes AS typeNotesDb FROM kpg_task t1, keypair_algorithm t2, sys_code t3, sys_param_type t4, sys_code t5, sys_param_type t6 WHERE t1.kpg_algorithm = t2.id AND t3.para_type = t4.id AND t5.para_type = t6.id AND t1.task_status = t3.id AND t1.dbcommit_bufsize = t5.id
id=#{id}
name=#{name}
keyPairAlgorithm=#{keyPairAlgorithm}
taskStartTime=#{taskStartTime}
